window.jsMethods = {
    showSuccessMessage: function (message) {
        Swal.fire({ icon: 'success', text: message, allowOutsideClick: false });
    },
    showErrorMessage: function (message) {
        Swal.fire({ icon: 'error', text: message, allowOutsideClick: false });
    },
    showLoadingMessage: function () {
        Swal.fire({
            text: 'Wait a few seconds.',
            onBeforeOpen: () => {
                Swal.showLoading();
            }
        });
    },
    closeMessage: function () {
        Swal.close();
    }
};
